import React, { useEffect, useState } from 'react'
import { Col, Container, Row, Modal, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { getAllCategory } from '../../actions'
import Layout from '../../components/Layout'
import Input from '../../components/UI/Input'

/**
* @author
* @function Category
**/

const Category = (props) => {

    const dispatch = useDispatch();
    const category = useSelector(state => state.category);
    const [show, setShow] = useState(false);
    const [categoryName, setCategoryName] = useState('');
    const [parentCategoryId, setParentCategoryId] = useState('');
    const [categoryImage, setCategoryImage] = useState('');



    useEffect(() => {
        console.log('Category.js');
        dispatch(getAllCategory());
    }, []);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const renderCategories = (categories) => {
        let categoryList = [];
        for (let category of categories) {
            categoryList.push(
                <li key={category.name}>
                    {category.name}
                    {category.children.length > 0 ? (<ul>{renderCategories(category.children)}</ul>) : null}
                </li>
            );
        }
        return categoryList;
    }
    const creatCategoryList = (categories, options = []) => {
        for(let category of categories){
            options.push({
                value: category._id,
                name: category.name
            });
            if(category.children.length > 0){
                creatCategoryList(category.children, options);
            }
        }
        console.log(options);
        return options;
    }

    const handleCategoryImage = (e) => {
        setCategoryImage(e.target.files[0]);
    }


    return (
        <Layout sidebar>
            <Container>
                <Row>
                    <Col md={12}>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <h3>Category</h3>
                            <button onClick={handleShow}>Add</button>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <Col md={12}>
                        <ul>
                            {renderCategories(category.categories)}
                            {/* {JSON.stringify(renderCategories(category.categories))} */}
                        </ul>
                    </Col>
                </Row>

            </Container>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new Category</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Input
                        label = {'Category Name'}
                        value = {categoryName}
                        placeholder = {'Category Name'}
                        onChange = {(e) => setCategoryName(e.target.value)}
                    />
                    <select 
                    className="form-control"
                    value={parentCategoryId}
                    onChange={ (e) => setParentCategoryId(e.target.value)}>
                        <option>Select Category</option>
                        {
                            creatCategoryList(category.categories).map(
                                option => <option key={option.value} value={option.value}> {option.name} </option>
                            )
                        }
                    </select>
                    <input className="form-control" type="file" name="categoryImage" onChange={handleCategoryImage} />
                </Modal.Body>
                <Modal.Footer>
                    
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </Layout>
    )

}

export default Category
