import { act } from "react-dom/test-utils";
import { authConstants } from "../actions/constants";

const initState = {
    name: 'thang'
};


export default (state = initState, action) => {
    switch(action.type){
        case authConstants.LOGIN_REQUEST:
            state = {
                ...state,
                ...action.payload
            }
            break;
    }

    return state;
}
