import { categoryContants } from '../actions/constants';
const initState = {
    catetgories: [],
    loading: false,
    error: null
};

export default (state = initState, action) => {

    switch(action.type){
        case categoryContants.GET_CATEGORY_SUCCESS:
            state = {
                ...state,
                catetgories: action.payload.catetgories
            }
            break;
    }
    return state;
}
