import axiosIntance from "../helpers/axios";
import { authConstants } from "./constants"

export const login = (user) => {

    console.log(user);
    return (dispatch) => {

        // const res = await axiosIntance.post('/admin/signin', {

        // });
        dispatch({ 
            type: authConstants.LOGIN_REQUEST,
            payload: {
                ...user
            }
        });
    }
}
