import React, { useState } from 'react'
import Layout from '../../components/Layout';
import { Col, Container, Row, Modal, Button } from 'react-bootstrap';
import Input from '../../components/UI/Input';
import { useSelector } from 'react-redux';

/**
* @author
* @function Products
**/

const Products = (props) => {

    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [desceription, setDescription] = useState('');
    const [categoryId, setCategoryId] = useState('');
    const [productPictures, setProductPictures] = useState([]);
    const [show, setShow] = useState(false);
    const category = useSelector(state => state.category);

    const handleClose = () => {
        setShow(false);
    }
    const handleShow = () => setShow(true);

    const creatCategoryList = (categories, options = []) => {
        for(let category of categories){
            options.push({
                value: category._id,
                name: category.name
            });
            if(category.children.length > 0){
                creatCategoryList(category.children, options);
            }
        }
        //console.log(options);
        return options;
    }
    return (
        <Layout sidebar>
            <Container>
                <Row>
                    <Col md={12}>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <h3>Product</h3>
                            <button onClick={handleShow}>Add</button>
                        </div>
                    </Col>
                </Row>
            </Container>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new Product</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Input
                        label={'Product Name'}
                        value={name}
                        placeholder={'Product Name'}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <Input
                        label={'Quantity'}
                        value={quantity}
                        placeholder={'Quantity'}
                        onChange={(e) => setQuantity(e.target.value)}
                    />
                    <Input
                        label={'Price'}
                        value={price}
                        placeholder={'Price'}
                        onChange={(e) => setPrice(e.target.value)}
                    />
                    <Input
                        label={'Description'}
                        value={desceription}
                        placeholder={'Description'}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <select 
                    className="form-control"
                    value={categoryId}
                    onChange={ (e) => setCategoryId(e.target.value)}>
                        <option>Select Category</option>
                        {
                            creatCategoryList(category.categories).map(
                                option => <option key={option.value} value={option.value}> {option.name} </option>
                            )
                        }
                    </select>
                </Modal.Body>
                <Modal.Footer>

                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </Layout>
    )

}

export default Products
