import { categoryContants } from '../actions/constants';
const initState = {
    categories: [],
    loading: false,
    error: null
};

const buildNewCategories = (categories, category) => {
    let myCategories = [];
    for(let cat of categories){
        myCategories.push({
            ...cat,
            children: cat.children && cat.children.length > 0 ?
            buildNewCategories(cat.children, category) : []
        });
    }
    return myCategories;
}

export default (state = initState, action) => {

    switch(action.type){
        case categoryContants.GET_CATEGORY_SUCCESS:
            state = {
                ...state,
                categories: action.payload.categories
            }
            break;
        case categoryContants.ADD_CATEGORY_REQUEST:
            state = {
                ...state,
                loading: true
            }
            break;
        case categoryContants.ADD_CATEGORY_SUCCESS:
            state = {
                ...state,
                categories: buildNewCategories(state.categories, action.payload.category),
                loading: false
            }
            break;
        case categoryContants.ADD_CATEGORY_FAILURE:
            state = {
                ...initState
            }
    }
    return state;
}
